def balanceOf721(w3, token, wallet, ignore):
    result = token.functions.balanceOf(w3.toChecksumAddress(wallet)).call()
    #print (result)
    return result

def balanceOf20(w3, token, wallet, ignore):
    result = int(token.functions.balanceOf(w3.toChecksumAddress(wallet)).call() /10**18)
    #print (result)
    return result


def stakedTokensLen(w3, token, wallet, ignore):
    result = len(token.functions.stakedTokensOf(w3.toChecksumAddress(wallet)).call())
    #print(result)
    return result

def balanceOf1155(w3, token, wallet, id):
    result = token.functions.balanceOf(w3.toChecksumAddress(wallet), id).call()
    #print rsult
    return result


